
public class NestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int a=1;
	int b=20;
	for (int i=a;a<=b;i++) {
		for(int k=1;k<=10;k++) {
			System.out.println(i+"*"+k+"="+i*k);
		}
		System.out.println();
		System.out.print("---------------");
	}
	}
}

		
		
		
		
		
		
		
		
		
		
		
	
